DROP TABLE pokedex_kanto;
DROP TABLE movedex_gen3;
DROP TABLE squirtle_moves;
DROP TABLE charmander_moves;

CREATE TABLE pokedex_kanto(
    NO INT,
    pokemon VARCHAR(10),
    type1 VARCHAR(10),
    type2 VARCHAR(10),
    classification VARCHAR(20),
    category VARCHAR(20),
    ability VARCHAR(20),
    male FLOAT,
    female FLOAT,
    height FLOAT,
    weight FLOAT
);

CREATE TABLE movedex_gen3(
    name VARCHAR(14),
    type VARCHAR(8),
    category VARCHAR(8),
    power INT,
    accuracy INT,
    PP INT,
    effect VARCHAR(200)
);

CREATE TABLE squirtle_moves (
    move VARCHAR(14),
    level INT
);

CREATE TABLE charmander_moves (
    move VARCHAR(14),
    level INT
);
