DROP TABLE pokedex_kanto;
DROP TABLE movedex_gen3;
DROP TABLE squirtle_moves;
DROP TABLE charmander_moves;

CREATE TABLE pokedex_kanto(
    NO INT,
    pokemon VARCHAR(10),
    type1 VARCHAR(10),
    type2 VARCHAR(10),
    classification VARCHAR(20),
    category VARCHAR(20),
    ability VARCHAR(20),
    male FLOAT,
    female FLOAT,
    height FLOAT,
    weight FLOAT
);

CREATE TABLE base_stats(
    NO INTEGER,
    pokemon VARCHAR(10),
    HP INTEGER,
    attack INTEGER,
    defense INTEGER,
    sp_attack INTEGER ,
    sp_defense INTEGER,
    speed INTEGER
);

CREATE TABLE movedex_gen3(
    name VARCHAR(14),
    type VARCHAR(8),
    category VARCHAR(8),
    power INT,
    accuracy INT,
    PP INT,
    effect VARCHAR(200)
);

CREATE TABLE natures(
    nature VARCHAR(7),
    increased_stat VARCHAR(11),
    decreased_stat VARCHAR(11),
    favorite_flavor VARCHAR(6),
    disliked_flavor VARCHAR(6)
);

CREATE TABLE squirtle_moves (
    move VARCHAR(14),
    level INT
);

CREATE TABLE charmander_moves (
    move VARCHAR(14),
    level INT
);
